import { useState, useEffect } from 'react'

import ProductDescription from "./ProductDescription/ProductDescription";
import * as productService from '../../services/productService'
import ProductImage from "./ProductImage.";
import ProductInfo from "./ProductInfo/ProductInfo";

const ProductById = (match) => {
    const [productId, setProductId] = useEffect(match.params.id);
    console.log(match)
    const [product, setProduct] = useState([]);
    useEffect(async () => {
        // await productService.GetById(productId)
        //     .then(res => setProduct(res));
        await setProduct(productService.GetById(productId));
    }, [])

    return (
        <div className="container">
            <div className="row mt-5 mb-5">
                <ProductImage imageUrl={product.imageUrl} />
                <ProductInfo name={product.name} categoryName={product.categoryName} originalPrice={product.price} id={product.id} />
                <ProductDescription description={product.description} />
            </div>
        </div>
    )
}

export default ProductById
